-- Create schema v1

CREATE TABLE devices (
  name text,
  mac text NOT NULL PRIMARY KEY CHECK (length(mac) >= 12),
  --last_access datetime DEFAULT CURRENT_TIMESTAMP,
  favourite boolean NOT NULL DEFAULT 0
);

CREATE TABLE visitors (
  id integer PRIMARY KEY,
  photo blob UNIQUE,
  name text CHECK (name <> ""),
  name_dev text,
  description text,
  descriptor blob NOT NULL UNIQUE CHECK (length(descriptor) = 1024)
);

CREATE TABLE access (
  mac text NOT NULL REFERENCES devices (mac),
  pid integer NOT NULL REFERENCES visitors (id),
  granted boolean,
  granted_dev boolean,
  PRIMARY KEY(mac, pid)
);

PRAGMA foreign_keys=ON;

