-- Upgrade to schema v2
CREATE TABLE NEW_devices (
  name text,
  mac text NOT NULL PRIMARY KEY CHECK (length(mac) >= 12),
  last_access datetime DEFAULT CURRENT_TIMESTAMP,
  favourite boolean NOT NULL DEFAULT 0
);

INSERT INTO NEW_devices (name, mac, favourite) SELECT name, mac, favourite FROM devices;

PRAGMA foreign_keys=OFF;

DROP TABLE devices;
ALTER TABLE NEW_devices RENAME TO devices;

PRAGMA foreign_keys=ON;

-- Delete tables
DROP TABLE IF EXISTS access;
DROP TABLE IF EXISTS devices;
DROP TABLE IF EXISTS visitors;
